public class Operators4 {

	static double velocity(double distance, double time) {
		return distance/time;
	}
	
	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(velocity(10.0, 20.0));
	}

}
